
import IFactory from '../fac';
import * as utility from 'utility';
import * as dpm from '../../dpm';

function facOfFac(ref: string): IFactory<string, number> {
    function dpid(pack: string): string { return `${ref}_${pack}` }

    return {
        make: (pack: string) => {
            return dpm.get
        },
        validate: (pack: string) => utility.validate.nonEmptyStr(pack),
        dpid: dpid
    }
}
