
import * as def from '../../def';
import * as datasrc from 'datasrc';
import {Action} from '../index';
import * as bb from 'bluebird';
import * as utility from 'utility';

interface WmRawAction {
    target: string;
    isIndex: boolean;
    start: Date | number;
    end: Date | number;
}

export function resolve(pack: WmRawAction, task: def.Task): bb<void> {

}

export function validate(pack: WmRawAction): boolean {
    if(!utility.validate.isStr(pack.target) || (pack.isIndex !== true && pack.isIndex !== false)) return false;
    const end = pack.end, start = pack.start;
    const e:Date = utility.validate.isNum(end) ? new Date(utility.date.dateTs2MsTs(end)) : end;
    const s:Date = utility.validate.isNum(start) ? new Date(utility.date.dateTs2MsTs(start)) : start;
}
