export function parseJson<T>(str: string): T {
    try {
        return <T>JSON.parse(str);
    }
    catch (e) {
        return null;
    }
}

export function toStr(input: any): string {
    return <string>input.toString();
}

export function randomStr() {
    return new Date().getTime() + '' + Math.floor(Math.random() * 10000000);
};

export {validate, date, array, file, http, mongo, num}
